  $('.popup').hide();
  overlay.appendTo(document.body).remove();
  return false;
  });

  $('.x').click(function(){
  $('.popup').hide();
  overlay.appendTo(document.body).remove();
  return false;
  });
  });

$('#pause').click(function(){
  $('#pause').hide();
  $('#play').show();
});

$('#play').click(function(){
  $('#play').hide();
  $('#pause').show();
});

