function timeago($timestamp)
{
	$time_ago=strtotime($timestamp);
	$current_time=time();
	$time_diff=$current_time - $time_ago;
	$seconds=$time_diff;
	$minutes=round($seconds / 60);
	$hours=round($seconds / 3600);
	$days=round($seconds / 86400);
	$weeks=round($seconds / 604800);
	$months=round($seconds / 2629440);
	$years=round($seconds / 31553280);
	
	if($seconds <= 60)
	{
		return "just now";
	}
	else if($minutes <=60)
	{
		if($minutes==1)
		{
			return "1 minute ago";
		}
		else
		{
			return "$minutes minutes ago";
		}
	}
	else if($hours<=24)
	{
		if($minutes==1)
		{
			return "an hour ago";
		}
		else
		{
			return "$hours hours ago";
		}
	}
	else if($days<=24)
	{
		if($days==1)
		{
			return "yesterday";
		}
		else
		{
			return "$days days ago";
		}
	}
	else if($weeks<=4.3)
	{
		if($weeks==1)
		{
			return "a week ago";
		}
		else
		{
			return "$weekss weeks ago";
		}
	}
	else if($months<=24)
	{
		if($months==1)
		{
			return "a month ago";
		}
		else
		{
			return "$months months ago";
		}
	}
	else if($years<=24)
	{
		if($years==1)
		{
			return "1 year ago";
		}
		else
		{
			return "$years years ago";
		}
	}
	
}

