    <script>
    function getlang(lang)
    {
      var lng;
      if(window.XMLHttpRequest)
      {
        lng=new XMLHttpRequest();
      }
      lng.onreadystatechange=function()
      {
        if(lng.readyState==4)
        {
          document.getElementById("data").innerHTML=lng.responseText;
        }
      }
      lng.open("POST","backend_book_publish.php?language="+lang,true);
      lng.send();
      
    }
    </script> 


