    <script>
        $(document).ready(function(){
            $('#calculate_emi').click(function(){
                $('#loan_emi').val('Rs. 0');
                
                var amount          = $('#loan_amount').val();
                var months          = $('#loan_months').val();
                var interest        = $('#loan_interest').val();
                var emi             = 0;
                var total           = 0;
                var conv_months     = 0;
                var year_months     = 12;
                var total_interest  = 0;
                var yearly_interest = 0;
                
                conv_months         = months/year_months;
                yearly_interest     = (amount*interest)/100;
                total_interest      = parseFloat(yearly_interest*conv_months);
                
                emi                 = parseFloat((parseFloat(total_interest)+parseFloat(amount))/parseFloat(months)).toFixed(2);
                
                // var output          = 'conv_months->'+conv_months+'/yearly_interest->'+yearly_interest+'/total_interest->'+total_interest+'/emi->'+emi;
                
                $('#loan_emi').val('Rs. '+emi);
                // $('#loan_emi').val(output);
            });
        });
    </script>
    
